import React, { Component } from "react";
import SearchBar from "./Components/SearchBar";
import Loader from "./Components/Loader";
import ImageGallery from "./Components/ImageGallery";
import { fetchImagesByQuerry } from "./services/galleryAPI";

export default class App extends Component {
  state = {
    images: [],
    querry: "",
    load
  };

  fetchImages = () => {
    fetchImagesByQuerry(this.state.querry, 1).then((res) =>
      this.setState({ images: res })
    );
  };

  handleSearch = (searchQuerry) => {
    this.setState({ querry: searchQuerry },
      this.fetchImages);
  };

  render() {
    const { images } = this.state;
    return (
      <div className="App">
        <SearchBar onSearch={this.handleSearch} /> <Loader />
        <ImageGallery images={images} />
      </div>
    );
  }
}
